package com.freelancer.leetcode;

import org.junit.Assert;
import org.junit.Test;

/**
 * Created by Soyee on 2016/5/25.
 */
public class LeetCode44Test {

    @Test
    public void testRegularMatching() {
        LeetCode44 leetCode44 = new LeetCode44();
        Assert.assertFalse(leetCode44.isMatch("ab", "*a"));
        Assert.assertFalse(leetCode44.isMatch("", "*a*"));
        Assert.assertFalse(leetCode44.isMatch("aa", "a"));
        Assert.assertTrue(leetCode44.isMatch("aa", "aa"));
        Assert.assertFalse(leetCode44.isMatch("aaa", "aa"));
        Assert.assertTrue(leetCode44.isMatch("aa", "*"));
        Assert.assertTrue(leetCode44.isMatch("aa", "a*"));
        Assert.assertTrue(leetCode44.isMatch("ab", "?*"));
        Assert.assertFalse(leetCode44.isMatch("aab", "c*a*b"));
        Assert.assertFalse(leetCode44.isMatch("aab", "c*a*b"));
        Long start = System.currentTimeMillis();
        leetCode44.isMatch("aaabbbaabaaaaababaabaaabbabbbbbbbbaabababbabbbaaaaba", "a********b");
        System.err.println("Consumed milliseconds : " + (System.currentTimeMillis() - start));
        start = System.currentTimeMillis();
        leetCode44.isMatch("abbabaaabbabbaababbabbbbbabbbabbbabaaaaababababbbabababaabbababaabbbbbbaaaabababbbaabbbbaabbbbababababbaabbaababaabbbababababbbbaaabbbbbabaaaabbababbbbaababaabbababbbbbababbbabaaaaaaaabbbbbaabaaababaaaabb",
                "**aa*****ba*a*bb**aa*ab****a*aaaaaa***a*aaaa**bbabb*b*b**aaaaaaaaa*a********ba*bbb***a*ba*bb*bb**a*b*bb");
        System.err.println("Consumed milliseconds : " + (System.currentTimeMillis() - start));
        start = System.currentTimeMillis();
        Assert.assertFalse(leetCode44.isMatch("",
                "**"));
        System.err.println("Consumed milliseconds : " + (System.currentTimeMillis() - start));
    }

    @Test
    public void testRegularMatching2() {
        LeetCode44 leetCode44 = new LeetCode44();
        Assert.assertTrue(leetCode44.isMatch("abefcdgiescdfimde", "ab*cd?i*de"));
        Assert.assertTrue(leetCode44.isMatch("c", "*?*"));
    }
}
